import styled from 'styled-components';

const HeaderStyles = styled.div`
    height: 60px;
    box-shadow: 0 6px 6px -6px rgba(0, 0, 0, 0.2);
    display: flex;
    align-items: center;

    img {
        margin-left: 30px;
        max-width: 50%;
        max-height: 50%;
    }
`;

export default HeaderStyles;
